mapper.update:
UPDATE user
SET banEndTime = DATE_ADD(NOW(), INTERVAL #{penaltiesTerm} DAY), status = 0
WHERE nickName = #{nickName};

UPDATE user
SET status = 1, banEndTime = null
WHERE id = #{id};

mapper.select:
SELECT * FROM report
ORDER BY reportedAt DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM report
WHERE DATE(reportedAt) = CURDATE();
