mapper.update:
UPDATE user
SET rating = #{avgScore}
WHERE id = #{userId}

mapper.select:
SELECT u.id, u.nickName
FROM userschedule us
JOIN user u ON us.userId = u.id
WHERE us.groupScheduleId = #{groupScheduleId}
AND us.status = 1
AND u.id != #{reviewer}

SELECT COUNT(*)
FROM review
WHERE groupScheduleId = #{groupScheduleId}
AND reviewer       = #{reviewer}
AND userId         = #{userId}
AND status = 1
